/**
 * Get compact rating info for search results context.

Public endpoint - no authentication required.
Returns rating and review count optimized for search result display.
 * @summary Get Search Rating
 */
export const getGetSearchRatingApiV1ReviewsInstructorInstructorIdSearchRatingGetUrl = (
  instructorId: string,
  params?: GetSearchRatingApiV1ReviewsInstructorInstructorIdSearchRatingGetParams
) => {
  const normalizedParams = new URLSearchParams();

  Object.entries(params || {}).forEach(([key, value]) => {
    if (value !== undefined) {
      normalizedParams.append(key, value === null ? 'null' : value.toString());
    }
  });

  const stringifiedParams = normalizedParams.toString();

  return stringifiedParams.length > 0
    ? `/api/v1/reviews/instructor/${instructorId}/search-rating?${stringifiedParams}`
    : `/api/v1/reviews/instructor/${instructorId}/search-rating`;
};

